interface Object {
    /**
     * オブジェクトをシャロ―コピーして返します。
     */
    shallowCopy(): object;
    /**
     * 値をコンテンツログに出力します。
     */
    cons(): void;
    /**
     * 値をチャットに出力します。
     */
    chat(): void;
}

